import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { animateScroll as scroll } from 'react-scroll';

const NavItem = ({ to, text }) => {
  const location = useLocation();

  const handleClick = () => {
    if (to === 'leaderboard') {
      // Always scroll to the top for the "FIXTURES & RESULTS" link
      scroll.scrollToTop({
        spy: true,
        smooth: true,
        offset: -70,
        duration: 1000,
      });
    } else {
      // For other items, navigate or scroll based on the location
      if (location.pathname !== '/') {
        window.location.href = '/';
      } else {
        scroll.scrollTo(to, {
          spy: true,
          smooth: true,
          offset: -70,
          duration: 1000,
        });
      }
    }
  };

  return (
    <a className="relative group">
      {to === 'leaderboard' ? (
        <Link
          to={to}
          className="cursor-pointer hover:text-gray-600 transition"
          onClick={handleClick}
        >
          {text}
        </Link>
      ) : (
        <Link
          to={to}
          spy={true}
          smooth={true}
          offset={-70}
          duration={1000}
          className="cursor-pointer hover:text-gray-600 transition"
        >
          {text}
        </Link>
      )}
      {to === 'leaderboard' && (
        <span className="absolute -bottom-1 left-0 w-0 h-1 bg-green-500 transition-all group-hover:w-full"></span>
      )}
    </a>
  );
};

const Header = () => {
  const logoUrl =
    'https://elitesportvendsyssel.dk/wp-content/uploads/2022/12/logo.png';

  return (
    <header className="p-4 flex justify-around items-center z-20">
      <div className="logo">
        <img src={logoUrl} alt="Logo" className="w-24 h-24" />
      </div>
      <nav className="space-x-4 text-gray-500 font-semibold">
        {[
          { to: '/', text: 'HOME' },
          { to: 'team', text: 'TEAM' },
          { to: 'leaderboard', text: 'FIXTURES & RESULTS' },
          { to: 'news', text: 'NEWS' },
          { to: 'vision', text: 'OUR VISION' },
          { to: 'sponsor', text: 'SPONSORS' },
        ].map((item) => (
          <NavItem key={item.to} to={item.to} text={item.text} />
        ))}
      </nav>
    </header>
  );
};

export default Header;
