import React from "react";
import { newsData } from '../Data/NewsData';
import { Link } from 'react-router-dom';

const NewsPage = () => {
  const itemsPerRow = 3;

  return (
    <div className="py-10">
      <div className='text-gray-500 py-5 mx-auto text-center'>
        <div className="text-5xl font-bold text-gray-500 underline decoration-green-500">News From Handball Club</div>
      </div>

      <div className="flex justify-between flex-wrap">
        {newsData.map((newsItem, index) => (
          <div key={newsItem.id} className={`w-96 bg-white border border-gray-200 ${index >= itemsPerRow ? 'mt-5' : ''}`}>
            <div className="relative max-w-96 overflow-hidden bg-cover bg-no-repeat">
              <img
                src={newsItem.img}
                alt={newsItem.title}
                className="w-96 max-w-96 h-52 transition duration-500 ease-in-out hover:transform hover:scale-110"
              />
            </div>
            <div className="p-4">
              <h2 className="text-xl font-semibold">{newsItem.title}</h2>
              <p className="text-gray-500">{newsItem.description}</p>
              <Link to={`/news/${newsItem.id}`} className="text-blue-500 hover:underline">
                Read more
              </Link>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default NewsPage;
