import React from 'react';
import { Link } from 'react-scroll';

const NavItem = ({ to, text, isLeaderboard }) => {
  const handleClick = () => {
    if (isLeaderboard) {
      // Sadece başa git, scroll yapma
      window.scrollTo({ top: 0, behavior: 'smooth' });
    }
  };

  return (
    <Link
      to={to}
      spy={true}
      smooth={true}
      offset={-70}
      duration={1000}
      onClick={handleClick}
      className="cursor-pointer hover:text-gray-600 transition"
    >
      {text}
    </Link>
  );
};

const Header = () => {
  const logoUrl = "https://elitesportvendsyssel.dk/wp-content/uploads/2022/12/logo.png";

  return (
    <header className="p-4 flex justify-between items-center z-20">
      <div className="logo">
        <a href="/">
          <img src={logoUrl} alt="Logo" className="w-24 h-24" />
        </a>
      </div>
      <nav className="space-x-4 text-gray-500 font-semibold">
        {[
          { to: 'team', text: 'TEAM' },
          { to: 'leaderboard', text: 'FIXTURES & RESULTS', isLeaderboard: true },
          { to: 'news', text: 'NEWS' },
          { to: 'vision', text: 'OUR VISION' },
          { to: 'sponsor', text: 'SPONSORS' },
        ].map((item) => (
          <NavItem key={item.to} to={item.to} text={item.text} isLeaderboard={item.isLeaderboard} />
        ))}
      </nav>
    </header>
  );
};

export default Header;
