import React from "react";
 import { useState } from "react";
 import { playerData } from "../Data/PlayerData";

const TeamPage = () => {
    const [display, setDisplay] = useState("Senior");

    const filteredPlayers = playerData.filter(
        (player) => player.team_type === display
      );

    return (
        <div className="flex flex-col justify-between items-center text-gray-500 py-5">
            <div className="text-5xl font-bold underline decoration-green-500 py-10">
                Handbool Team SC
            </div>
            <div className="py-5">
                <button
                className={`button text-2xl px-4 font-bold border-b-4 ${
                    display === "Senior" ? "border-green-500" : ""
                } transition duration-300`}
                onClick={() => setDisplay("Senior")}
                >
                Senior
                </button>
                <button
                className={`button text-2xl px-4 font-bold border-b-4 ${
                    display === "U19" ? "border-green-500" : ""
                } transition duration-300`}
                onClick={() => setDisplay("U19")}
                >
                U19
                </button>
                <button
                className={`button text-2xl px-4 font-bold border-b-4 ${
                    display === "Backteam" ? "border-green-500" : ""
                } transition duration-300`}
                onClick={() => setDisplay("Backteam")}
                >
                Backteam
                </button>
            </div>

            <div>
                <div className="text-3xl font-bold underline decoration-green-500 py-5">Goalkeeper</div>
            </div>
            <div className="flex flex-row">
                {filteredPlayers.map((player) => (
                    <>
                    <div class=" max-w-sm bg-white border border-gray-200 rounded-lg shadow ">
                        <div class="flex flex-col items-center pb-10">
                            <img class="w-24 h-24 mb-3 rounded-full shadow-lg" src={player.player_img} alt={player.name}/>
                            <h5 class="mb-1 text-xl font-medium text-gray-900">Bonnie Green</h5>
                            <span class="text-sm text-gray-500 dark:text-gray-400">Visual Designer</span>
                            <div className="flex items-center justify-center">
                                <img src={player.sponsor} className="w-1/2 p-4" alt="Sponsor"></img>
                            </div>
                        </div>
                    </div>



                    </>
                    ))}
            </div>
        </div>
    );
};

export default TeamPage;
