import React, {useState} from "react";
import rema from '../img/rema.png'
import SponsorData from '../Data/SponsorData'

const SponsorPage = () => {
    const [display, setDisplay] = useState("MainSponsor");

    const filteredSponsors = SponsorData.filter(
        (sponsor) => sponsor.sponsor_type === display
      );
  return (
    <>
        <div className="py-10">
            <div className='text-gray-500 py-5 mx-auto text-center'>
                <div className=" text-5xl font-bold text-gray-500 underline decoration-green-500 ">Sponsors</div>
            </div>

            <div className="flex justify-center items-center text-gray-500 py-10">

                <button
                className={`button text-2xl px-4 font-bold border-b-4 ${
                    display === "MainSponsor" ? "border-green-500" : ""
                } transition duration-300`}
                onClick={() => setDisplay("MainSponsor")}
                >
                Main Sponsors
                </button>
                <button
                className={`button text-2xl px-4 font-bold border-b-4 ${
                    display === "EliteSponsors" ? "border-green-500" : ""
                } transition duration-300`}
                onClick={() => setDisplay("EliteSponsors")}
                >
                Elite Sponsors
                </button>
                <button
                className={`button text-2xl px-4 font-bold border-b-4 ${
                    display === "LocalSponsors" ? "border-green-500" : ""
                } transition duration-300`}
                onClick={() => setDisplay("LocalSponsors")}
                >
                Local Sponsors
                </button>
             </div>

             <div className="flex flex-wrap">
                <img src={rema} alt="Image 1" className="w-1/3 p-5" />
                <img src={rema} alt="Image 2" className="w-1/3 p-5" />
                <img src={rema} alt="Image 3" className="w-1/3 p-5" />
                <img src={rema} alt="Image 4" className="w-1/3 p-5" />
                <img src={rema} alt="Image 5" className="w-1/3 p-5" />
                <img src={rema} alt="Image 6" className="w-1/3 p-5" />
                <img src={rema} alt="Image 7" className="w-1/3 p-5" />
                <img src={rema} alt="Image 8" className="w-1/3 p-5" />
                <img src={rema} alt="Image 9" className="w-1/3 p-5" />
                <img src={rema} alt="Image 10" className="w-1/3 p-5" />
                <img src={rema} alt="Image 11" className="w-1/3 p-5" />
                </div>

        </div>
    </>
  );
};

export default SponsorPage;
